SELECT U1.n_name as n_name, U2.n_name as n_name2 from ((SELECT n_name, o_orderkey FROM customer_bg c, orders_bg o, nation_bg n WHERE c_nationkey = n_nationkey AND c_custkey = o_custkey AND n_name = 'IRAQ') U1 JOIN (SELECT n_name, o_orderkey FROM supplier_bg s, orders_bg o, lineitem_bg l, nation_bg n WHERE s_nationkey = n_nationkey AND o_orderkey = l_orderkey AND s_suppkey = l_suppkey AND n_name = 'GERMANY') U2 ON u1.o_orderkey=u2.o_orderkey);